import React, { useEffect } from "react";
import PopularPosts from "../components/PopularPosts";
import PostItem from "../components/PostItem";

import { useDispatch, useSelector } from "react-redux";
import { getAllPosts } from "../redux/features/post/postSlice";

const MainPage = () => {
  const dispatch = useDispatch();
  const { posts, populatPosts } = useSelector((state) => state.post);

  useEffect(() => {
    dispatch(getAllPosts());
  }, [dispatch]);

  if (!posts.length) {
    return <h1 className="header">No Posts</h1>;
  }

  return (
    <div className="ui center aligned two column grid">
      <div className="twelve wide center aligned column">
        <div className="ui inverted list">
          <PostItem />
          <PostItem />
          <PostItem />
          <PostItem />
        </div>
      </div>
      <div className="four wide column">
        <h2 className="header">Popular:</h2>
        <div className="ui link cards">
          <PopularPosts />
          <PopularPosts />
          <PopularPosts />
          <PopularPosts />
          <PopularPosts />
        </div>
      </div>
    </div>
  );
};

export default MainPage;
