import axios from "../utils/axios";
import React, { useCallback, useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import PostItem from "../components/PostItem";
import { Link, useNavigate, useParams } from "react-router-dom";

import { toast } from "react-toastify";
import { removePost } from "../redux/features/post/postSlice";
import {
  createComment,
  getAllComments,
} from "../redux/features/comment/commentSlice";
import CommentItem from "../components/CommentItem";

const PostPage = () => {
  const [post, setPost] = useState(null);
  const [comment, setComment] = useState("");

  const { user } = useSelector((state) => state.auth);
  const { comments } = useSelector((state) => state.comment);

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const params = useParams();

  const removePostHandler = () => {
    try {
      dispatch(removePost(params.id));
    } catch (error) {
      console.log(error);
    }
    navigate("/");
    toast.warn("Post deleted");
  };

  const handleSubmit = () => {
    try {
      const postId = params.id;
      dispatch(createComment({ postId, comment }));
      setComment("");
    } catch (error) {
      console.log(error);
    }
  };

  const fetchComments = useCallback(async () => {
    try {
      dispatch(getAllComments(params.id));
    } catch (error) {
      console.log(error);
    }
  }, [params.id, dispatch]);

  const fecthPost = useCallback(async () => {
    const { data } = await axios.get(`/posts/${params.id}`);
    setPost(data);
  }, [params.id]);

  useEffect(() => {
    fecthPost();
  }, [fecthPost]);

  useEffect(() => {
    fetchComments();
  }, [fetchComments]);

  return (
    <div>
      <div>
        <button
          style={{ marginBottom: "40px" }}
          className="ui inverted large button"
        >
          Back
        </button>
        {user?._id === post?.author && (
          <React.Fragment>
            <button
              onClick={removePostHandler}
              className="ui right floated inverted red large button"
            >
              Delete
            </button>
            <button
              onClick={() => navigate(`/post/${params.id}/edit`)}
              className="ui right floated inverted orange large button"
            >
              Edit
            </button>
          </React.Fragment>
        )}
      </div>

      <div className="ui center aligned two column grid">
        <div className="twelve wide column">
          <div className="ui inverted list">
            <PostItem post={post} />
          </div>
        </div>
        <div className="four wide column">
          <div className="ui segment">
            <form onSubmit={(e) => e.preventDefault()} className="ui form">
              <div className="field">
                <div className="ui acrion input">
                  <input
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    type="text"
                    placeholder="Comment"
                  />
                  <button
                    onClick={handleSubmit}
                    className="ui right floated inverted button"
                  >
                    Send
                  </button>
                </div>
              </div>
            </form>
            <div className="ui  comments">
              {comments?.map((comment) => {
                return <CommentItem key={comment._id} comment={comment} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostPage;
