import axios from "../utils/axios";
import React, { useCallback, useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import PostItem from "../components/PostItem";
import { Link, useParams } from "react-router-dom";

import { toast } from "react-toastify";

const PostPage = () => {
  const [post, setPost] = useState(null);

  const { status } = useSelector((state) => state.auth);
  const { user } = useSelector((state) => state.auth);

  const dispatch = useDispatch();
  const params = useParams();

  const fecthPost = useCallback(async () => {
    const { data } = await axios.get(`/posts/${params.id}`);
    setPost(data);
  }, [params.id]);

  useEffect(() => {
    fecthPost();
    if (status?.type === "error") {
      toast.error(status.content);
    } else if (status.type === "warning") {
      toast.worn(status?.content);
    }
  }, [fecthPost]);

  return (
    <div>
      <div>
        <Link to={"/"}>
          <button
            style={{ marginBottom: "40px" }}
            className="ui inverted large button"
          >
            Back
          </button>
        </Link>
        {user?._id === post?.author && (
          <div className="ui right floated egment">
            <button className="ui  inverted red large button">Delete</button>
            <button className="ui inverted yellow large button">Edit</button>
          </div>
        )}
      </div>

      <div className="ui center aligned two column grid">
        <div className="twelve wide column">
          <div className="ui inverted list">
            <PostItem post={post} />
          </div>
        </div>
        <div className="four wide column">COMMENTS</div>
      </div>
    </div>
  );
};

export default PostPage;
