import React from "react";

const PostItem = ({ post }) => {
  return (
    <div style={{ width: "70%", margin: "0 auto" }} class="item">
      <div class="content">
        <div className="image">
          <img src="" alt="" />
        </div>
        <a class="header">{post.title}</a>
        <div class="description">
          <p>{post.text}</p>
        </div>
        <div class="extra">
          <p className="right floated ">
            <i class="heart outline like icon"></i>
            {post.views}
          </p>
          <p className="right floated ">
            <i class="comment icon"></i>
            {post.comments?.length}
          </p>
        </div>
      </div>
    </div>
  );
};

export default PostItem;
