import React from "react";
import { Link, NavLink } from "react-router-dom";

const Navbar = () => {
  const isAuth = true;

  const returnMenu = () => {
    return isAuth ? (
      <>
        <NavLink to={"/posts"} className="item">
          My Blogs
        </NavLink>
        <NavLink to={"/new"} className="item">
          Create
        </NavLink>
      </>
    ) : (
      ""
    );
  };

  return (
    // <div className="ui vertical inverted segment">
    <div className="ui large secondary inverted pointing menu">
      <div className="three column centered ui grid container">
        <div className="three wide column item">MERN-BLOG</div>
        <div
          style={{ justifyContent: "center" }}
          className="ten wide column item"
        >
          <NavLink to={"/"} className="item">
            Main
          </NavLink>
          {returnMenu()}
        </div>
        <div className="three wide column right item">
          {!isAuth ? (
            <button className="ui inverted button">Login</button>
          ) : (
            <button className="ui inverted button">Logout</button>
          )}
        </div>
      </div>
    </div>
    // </div>
  );
};

export default Navbar;
