import React, { useEffect } from "react";
import { useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { isLogedIn } from "../redux/features/auth/authSlice";
import { toast } from "react-toastify";

const AuthFrom = ({
  username,
  setUsername,
  password,
  setPassword,
  onSubmit,
  header,
}) => {
  const { status } = useSelector((state) => state.auth);
  const isAuth = useSelector(isLogedIn);

  const navigate = useNavigate();

  useEffect(() => {
    if (isAuth) {
      navigate("/");
    }
    if (status && status.type === "error") {
      toast.error(status.content);
    } else if (status && status.type === "success") {
      toast.success(status.content);
      setUsername("");
      setPassword("");
    }
  }, [status, isAuth, navigate, setUsername, setPassword]);

  return (
    // <div
    //   style={{ height: "90vh" }}
    //   className="ui middle aligned center aligned grid"
    // >
    //   <div className="eight wide column">
    //     <h1 className="header">{header}</h1>
    //     <div className="ui stacked segment">
    <form onSubmit={(e) => onSubmit(e)} className="ui form">
      <div
        className={`field ${status && status.type === "error" ? "error" : ""}`}
      >
        <div className="ui left icon input">
          <i className="user icon" />
          <input
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            type="text"
            placeholder="Username"
            required
          />
        </div>
      </div>
      <div
        className={`field ${status && status.type === "error" ? "error" : ""}`}
      >
        <div className="ui left icon input">
          <i className="lock icon" />
          <input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            placeholder="Password"
            required
          />
        </div>
      </div>
      <button className="large ui fluid secondary submit button">
        {header}
      </button>
    </form>
    //     </div>
    //   </div>
    // </div>
  );
};

export default AuthFrom;
