import React, { useState } from "react";
import "./AddPostPage.css";
import { useDispatch } from "react-redux";
import { createPost } from "../redux/features/post/postSlice";

const AddPostPage = () => {
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");
  const [image, setImage] = useState("");
  const dispatch = useDispatch();

  const onSubmit = (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      data.append("title", title);
      data.append("post", post);
      data.append("image", image);
      dispatch(createPost(data));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div
      style={{ height: "90vh" }}
      className="ui middle aligned center aligned grid"
    >
      <div className="twelve wide column">
        <h1 className="header">Create Post</h1>
        <form onSubmit={(e) => onSubmit(e)} className="ui form">
          <div className="ui inverted stacked segment">
            <div className="field">
              <div className="ui action input">
                <label
                  style={{
                    width: "100%",
                    padding: "20px",
                    justifyContent: "center",
                    color: "#ccc",
                  }}
                  htmlFor="file"
                  className="ui icon inverted button"
                >
                  <i className="file icon"></i>
                  Open File
                </label>
                <input
                  onChange={(e) => setImage(e.target.files[0])}
                  type="file"
                  id="file"
                  style={{ display: "none" }}
                />
              </div>
            </div>
            <div className="inverted field">
              <label>Title</label>
              <div className="addPostInput ui transparent inverted input">
                <input
                  style={{ color: "white" }}
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  type="text"
                  placeholder="Title"
                  required
                />
              </div>
            </div>
            <div className="field">
              <label>Post text</label>
              <textarea
                style={{
                  backgroundColor: "#1b1c1d",
                  border: "1px solid #ccc",
                  color: "white",
                }}
                value={text}
                onChange={(e) => setText(e.target.value)}
                rows="4"
                placeholder="Post Text"
              />
            </div>
          </div>
          <button className="ui large inverted button">Create</button>
          <button className="ui large inverted red button">Cancel</button>
        </form>
      </div>
    </div>
  );
};

export default AddPostPage;
