import React, { useEffect } from "react";
import PopularPosts from "../components/PopularPosts";
import PostItem from "../components/PostItem";

import { useDispatch, useSelector } from "react-redux";
import { getAllPosts } from "../redux/features/post/postSlice";

import { toast } from "react-toastify";

const MainPage = () => {
  const dispatch = useDispatch();
  const { posts, populatPosts } = useSelector((state) => state.post);
  const { status } = useSelector((state) => state.post);
  useEffect(() => {
    if (status && status.type === "error") {
      toast.error(status.content);
      status = null;
    } else if (status && status.type === "success") {
      toast.success(status.content);
      status = null;
    } else if (status && status.type === "warning") {
      toast.warn(status.content);
      status = null;
    }
  }, [status]);

  useEffect(() => {
    dispatch(getAllPosts());
  }, []);

  if (!posts.length) {
    return <h3 className="header">NO POSTS</h3>;
  }

  return (
    <div className="ui two column grid">
      <div className="twelve wide column">
        <div className="ui inverted divided list" style={{ cursor: "pointer" }}>
          {posts?.map((post, index) => (
            <PostItem key={index} post={post} />
          ))}
        </div>
      </div>
      <div className="four wide column">
        <h2 className="header">Popular:</h2>
        <div className="ui link cards">
          {populatPosts?.map((post, index) => (
            <PopularPosts key={index} post={post} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MainPage;
