import React from "react";
import { Link, NavLink } from "react-router-dom";

const Navbar = () => {
  const isAuth = true;

  const returnMenu = () => {
    return isAuth ? (
      <>
        <NavLink to={"/posts"} className="item column">
          My Blogs
        </NavLink>
        <NavLink to={"/new"} className="item column">
          Create
        </NavLink>
      </>
    ) : (
      ""
    );
  };

  return (
    <div className="ui vertical inverted segment">
      <div className="ui large secondary inverted pointing menu">
        <div className="ui center aligned container">
          <div className="item">MERN-BLOG</div>
          <div
            style={{ maxWidth: "100%" }}
            className="ui four column centered grid item"
          >
            <NavLink to={"/"} className="item column">
              Main
            </NavLink>
            {returnMenu()}
          </div>
          {/* <div className="right item">
            {!isAuth ? (
              <button className="ui inverted button">Login</button>
            ) : (
              <button className="ui inverted button">Logout</button>
            )}
          </div> */}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
